import React, { useState } from "react";

const SearchBar = ({ searchTerm, onSearch }) => (
  <input
    type="text"
    placeholder="Tìm kiếm khoá học..."
    value={searchTerm}
    onChange={(e) => onSearch(e.target.value)}
  />
);

export default SearchBar;
