import React from "react";
import { Link, useParams } from "react-router-dom";
import courses from "./data";
import "./coursedetail.css";
import Header from "../../Header/Header";
import Slidetop from "../../Slidetop/Slidetop";

const CourseDetail = () => {
  const { id } = useParams();
  const course = courses.find((c) => c.id === parseInt(id, 10));
  console.log(course);
  const breadcrumb = [
    { label: "Trang chủ", path: "/" },
    { label: "Danh sách khoá học", path: "/" },
    { label: course.name, path: "/courses/name" },
  ];
  if (!course) {
    return <p>Khoá học không tồn tại.</p>;
  }

  return (
    <div className="course-detail">
      <Header />
      <Slidetop />
      <nav>
        <ul className="breadcrumb">
          {breadcrumb.map((item, index) => (
            <li key={index}>
              {index < breadcrumb.length - 1 ? (
                <Link to={item.path}>{item.label}</Link>
              ) : (
                <span>{item.label}</span>
              )}
            </li>
          ))}
        </ul>
      </nav>
      <div className="course-interview">
        {/* <div className="course-name">{course.name}</div> */}
        <div className="course-name">
          <i class="fa fa-graduation-cap" aria-hidden="true">
            {course.name}
          </i>
        </div>
        <div className="course-description">{course.description}</div>
        <div className="course-description">{course.detail}</div>
        <div className="course-price">${course.price}</div>
      </div>
      {/* <img src={course.image} alt={course.name} /> */}
    </div>
  );
};

export default CourseDetail;
