import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Validation from "./LoginValidation";
import axios from "axios";
import { decode } from "jwt-decode";

function Login() {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });
  const [errors, setErrors] = useState({});
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();
  const handleInput = (event) => {
    setValues((prev) => ({
      ...prev,
      [event.target.name]: [event.target.value],
    }));
  };
  const handleSubmit = async (event) => {
    event.preventDefault();
    setErrors(Validation(values));
    if (errors.email === "" && errors.password === "") {
      axios
        .post("http://localhost:8081/login", values)
        .then((res) => {
          if (res.data === "Success") {
            navigate("/home");
          } else {
            setErrorMessage("Email hoặc mật khẩu không đúng");
          }
        })
        .catch((err) => console.log(err));
    }
  };

  const handleLogin = async () => {
    try {
      // Thực hiện đăng nhập và nhận JWT
      const response = await axios.post("http://localhost:8081/login", {
        email: "your_email@example.com", // Thay thế bằng thông tin đăng nhập thực tế
        password: "your_password", // Thay thế bằng thông tin đăng nhập thực tế
      });

      const token = response.data.token;

      // Lưu token vào localStorage hoặc cookie
      localStorage.setItem("token", token);

      // Giải mã token để lấy thông tin người dùng
      const decodedToken = jwt_decode(token);
      setEmail(decodedToken.email);
    } catch (error) {
      console.error("Login failed", error);
    }
  };

  return (
    <section className="">
      <div className="px-4 py-5 pt-5 px-md-5 mx-auto text-center text-lg-start">
        <div className="container">
          <div className="row gx-lg-5 align-items-center">
            <div className="col-lg-6 mb-5 mb-lg-10">
              <h1 className="my-5 display-3 fw-bold ls-tight">
                CÔNG TY GIÁO DỤC <br />
                <span className="text-success">PROTECH</span>
              </h1>
              <p style={{ color: "hsl(217, 10%, 50.8%)" }}>
                Hệ thống Website học trực tuyến
              </p>
            </div>

            <div className="col-lg-6 mb-5 mb-lg-0 ">
              <div className="card">
                <div className="card-body py-5 px-md-5 bg-light">
                  <form action="" onSubmit={handleSubmit}>
                    <div className="form-outline mb-4">
                      <label className="form-label" htmlFor="email">
                        <strong>Email</strong>
                      </label>
                      <input
                        type="email"
                        placeholder="Nhập Email"
                        name="email"
                        onChange={handleInput}
                        className="form-control"
                      />
                      {errorMessage && (
                        <div style={{ color: "red" }}>{errorMessage}</div>
                      )}
                      {errors.email && (
                        <span className="text-danger">{errors.email}</span>
                      )}
                    </div>

                    <div className="form-outline mb-4">
                      <label className="form-label" htmlFor="password">
                        <strong>Mật Khẩu</strong>
                      </label>
                      <input
                        type="password"
                        placeholder="Nhập Mật Khẩu"
                        name="password"
                        onChange={handleInput}
                        className="form-control"
                      />
                      {errors.password && (
                        <span className="text-danger">{errors.password}</span>
                      )}
                    </div>

                    <div className="form-check d-flex justify-content-center mb-4">
                      <input
                        className="form-check-input me-2"
                        type="checkbox"
                        value=""
                      />
                      <label className="form-check-label">
                        Ghi nhớ đăng nhập
                      </label>
                    </div>

                    <div>
                      {email ? (
                        <p>Welcome, {email}!</p>
                      ) : (
                        <button onClick={handleLogin}>Login</button>
                      )}
                    </div>
                    {/* <div className="d-flex justify-content-between">
                      <button type="submit" className="btn btn-success">
                        Đăng nhập
                      </button>

                      <Link to="./Signup" className="btn btn-secondary">
                        Đăng ký
                      </Link>
                    </div> */}

                    <div className="text-center">
                      <p>Hoặc đăng nhập với</p>
                      <button
                        className="btn btn-primary"
                        style={{ backgroundColor: "#dd4b39" }}
                        type="submit"
                      >
                        <i className="fa fa-google"></i> Google
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Login;
