import React from "react";
import courses from "../course/data";
import { useLocation } from "react-router-dom";

const SearchPage = () => {
  const location = useLocation();
  const searchKey = new URLSearchParams(location.search).get("key");
  const [searchResults, setSearchResults] = useState([]);
  useEffect(() => {
    // Simulate an asynchronous search request
    const searchRequest = new Promise((resolve) => {
      setTimeout(() => {
        const results = courses.filter((course) =>
          course.name.toLowerCase().includes(searchKey.toLowerCase())
        );
        resolve(results);
      }, 1000); // Simulate a 1-second delay for demonstration
    });

    // Update the state with search results
    searchRequest.then((results) => setSearchResults(results));
  }, [searchKey]);
  return (
    <div>
      <h2>Kết Quả Tìm Kiếm cho: {searchKey}</h2>
      {/* Hiển thị danh sách khoá học hoặc thông báo tìm kiếm không có kết quả */}
    </div>
  );
};

export default SearchPage;
