import React, { useEffect, useState } from "react";
import "./Header.css";
import axios from "axios";
import { NavLink, useNavigate } from "react-router-dom";
import SearchBar from "./SearchBar";

const Header = () => {
  const [name, setName] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showOptions, setShowOptions] = useState(false);
  const navigate = useNavigate;
  const handleLogout = () => {
    // Thực hiện logic đăng xuất
    console.log("Đăng xuất");
  };
  const handleProfileClick = () => {
    // Hiển thị hoặc ẩn tùy chọn khi người dùng nhấn vào thông tin cá nhân
    setShowOptions(!showOptions);
  };

  axios.defaults.withCredentials = true;
  useEffect(() => {
    axios
      .get("http://localhost:8081/name")
      .then((res) => {
        if (res.data.valid) {
          setName(res.data.name);
          setIsLoggedIn(true);
        } else {
          navigate("/login");
        }
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className="header">
      <div className="header-logo">
        <img
          className="logo"
          src="https://techinplay.edu.vn/assets/desktop/images/logo.png"
        />
      </div>
      <div className="search-bar">
        <SearchBar />
      </div>
      <div className="user-info">
        {isLoggedIn ? (
          <p onClick={handleProfileClick}>Xin chào, {name}!</p>
        ) : (
          <NavLink to="/login" className="btn btn-primary">
            Đăng nhập
          </NavLink>
        )}
        {showOptions && (
          <div className="options">
            <ul>
              <li onClick={() => console.log("Hiển thị thông tin cá nhân")}>
                Thông tin cá nhân
              </li>
              <li onClick={() => console.log("Hiển thị khóa học của tôi")}>
                Khóa học của tôi
              </li>
              <li onClick={handleLogout}>Đăng xuất</li>
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Header;
