import React, { useEffect, useState } from "react";
import "./Header.css";
import axios from "axios";
import { NavLink, useNavigate } from "react-router-dom";
import SearchBar from "./SearchBar";

const Header = () => {
  const [name, setName] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate;
  const handleLogout = () => {
    // Thực hiện logic đăng xuất
    console.log("Đăng xuất");
  };
  const handleProfileClick = () => {
    // Hiển thị các tùy chọn khi người dùng nhấn vào thông tin cá nhân
    console.log(
      "Hiển thị tùy chọn: Thông tin cá nhân, Khóa học của tôi, Đăng xuất"
    );
  };

  axios.defaults.withCredentials = true;
  useEffect(() => {
    axios
      .get("http://localhost:8081/name")
      .then((res) => {
        if (res.data.valid) {
          setName(res.data.name);
          setIsLoggedIn(true);
        } else {
          navigate("/login");
        }
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className="header">
      <div className="header-logo">
        <img
          className="logo"
          src="https://techinplay.edu.vn/assets/desktop/images/logo.png"
        />
      </div>
      <div className="search-bar">
        <SearchBar />
      </div>
      <div className="user-info">
        {isLoggedIn ? (
          <p onClick={handleProfileClick}>Xin chào, {name}!</p>
        ) : (
          <NavLink to="/login" className="btn btn-primary">
            Đăng nhập
          </NavLink>
        )}
        <div className="dropDown-user">
          <a href="/user">Thông tin cá nhân</a>
          <a href="/user/course">Khoá học của tôi</a>
          <a href="/login" onClick={handleLogout}>
            Đăng xuất
          </a>
        </div>
      </div>
    </div>
  );
};

export default Header;
