                      <Link to="/login" className="btn btn-secondary">
                        Đăng nhập
                      </Link>
                    </div>

                    <div className="text-center">
                      <p>Hoặc đăng nhập với</p>
                      <button
                        class="btn btn-primary"
                        style={{ backgroundColor: "#dd4b39" }}
                        type="submit"
                      >
