import React, { useEffect, useState } from "react";
import "./Header.css";
import axios from "axios";
import { Link, NavLink, useNavigate } from "react-router-dom";
import SearchBar from "./SearchBar";

const Header = () => {
  const [name, setName] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate;
  const logout = () => {
    window.localStorage.clear("connect.sid");
  };

  axios.defaults.withCredentials = true;
  useEffect(() => {
    axios
      .get("http://localhost:8081/name")
      .then((res) => {
        if (res.data.valid) {
          setName(res.data.name);
          setIsLoggedIn(true);
        } else {
          navigate("/login");
        }
      })
      .catch((err) => console.log(err));
  }, []);

  const checkLoginStatus = () => {
    // Thực hiện logic kiểm tra đăng nhập, set biến trạng thái isLoggedIn và username
    // setIsLoggedIn(true);
    // setUsername('Tên người dùng');
  };

  return (
    <div className="header">
      <div className="header-logo">
        <img
          className="logo "
          src="https://techinplay.edu.vn/assets/desktop/images/logo.png"
        />
      </div>
      <div className="search-bar">
        <SearchBar />
      </div>
      <div className="user-info">
        {isLoggedIn ? (
          <p>Xin chào, {name}!</p>
        ) : (
          <NavLink to="/login" className="btn btn-secondary">
            Đăng ký
          </NavLink>
        )}
        <div className="dropDown-user">
          <a href="#">Thông tin cá nhân</a>
          <a href="/user/course">Khoá học của tôi</a>
          <a href="/login" onClick={logout}>
            Đăng xuất
          </a>
        </div>
      </div>
    </div>
    // <nav className="navbar navbar-expand-lg navbar-light bg-light">
    //   <button
    //     className="navbar-toggler"
    //     type="button"
    //     data-toggle="collapse"
    //     data-target="#navbarTogglerDemo01"
    //     aria-controls="navbarTogglerDemo01"
    //     aria-expanded="false"
    //     aria-label="Toggle navigation"
    //   >
    //     <span className="navbar-toggler-icon"></span>
    //   </button>
    //   <div className="collapse navbar-collapse" id="navbarTogglerDemo01">
    //     <a className="navbar-brand" href="#">
    //       <img
    //         className="logo "
    //         src="https://techinplay.edu.vn/assets/desktop/images/logo.png"
    //       />
    //     </a>
    //     <form className="form-inline my-2 my-lg-0">
    //       <input
    //         className="form-control mr-sm-2"
    //         type="search"
    //         placeholder="Tìm kiếm khoá học..."
    //         aria-label="Search"
    //       />
    //       <button
    //         className="btn btn-outline-success my-2 my-sm-0"
    //         type="submit"
    //       >
    //         Search
    //       </button>
    //     </form>
    //     <div className="d-flex d-grid ma">
    //       <button class="btn btn-primary me-md-2" type="button">
    //         Đăng nhập
    //       </button>
    //       <button class="btn btn-primary" type="button">
    //         Đăng ký
    //       </button>
    //     </div>
    //   </div>
    // </nav>
  );
};

export default Header;
